css:
.styleguide {
  position: relative;
  display: flex;
  flex-direction: column;
}
.styleguide-controls {
  position: absolute;
  right: 6px;
  top: 6px;
  z-index: 100;
}
.styleguide-header {
  padding: 9px;
  border-bottom: 1px solid transparent;
}
.styleguide-header h1 {
  font-size: 2em;
  margin: 0 0 .5em 0;
  color: #f18260;
}
.styleguide-header p {
  font-size: 1.2em;
}
.styleguide-header p:last-of-type {
  margin-bottom: 0;
}
.styleguide-sections {
  flex: 1;
  overflow: auto;
}
.styleguide-sections > section {
  background-color: #232323;
  padding: 0;
  border-bottom: 1px solid transparent;
  border-top: none;
}
.styleguide-sections > section:last-child {
  margin-bottom: 0;
}
.styleguide-sections > section.collapsed {
  background-color: #282828;
}
.styleguide-sections > section.collapsed > .section-heading {
  display: block;
  margin: 0;
  padding-bottom: 9px;
  color: rgba(255, 255, 255, 0.8);
}
.styleguide-sections > section.collapsed > .section-heading:hover {
  color: #f18260;
  background-color: transparent;
}
.styleguide-sections > section.collapsed > .section-heading:active {
  background-color: #282828;
}
.styleguide-sections > section.collapsed > * {
  display: none;
}
.styleguide-sections .section-heading.section-heading {
  padding: 9px 9px 0 9px;
  cursor: pointer;
  font-weight: normal;
  font-size: 1.8em;
  color: #f18260;
}
.styleguide-sections section > h2 {
  font-size: 1.5em;
  line-height: 1.2;
  margin: 1em 9px 0 9px;
  color: #f18260;
}
.styleguide-sections section > p {
  font-size: 1.1em;
  margin: 0.5em 9px 1em 9px;
}
.styleguide .example {
  display: flex;
  flex-wrap: wrap;
  border-radius: 0;
  padding: 3px;
}
.styleguide .example .example-rendered,
.styleguide .example .example-code {
  position: relative;
  flex: 1 1 300px;
  min-width: 0;
  margin: 3px;
  border-radius: 0;
  border: 1px solid transparent;
}
.styleguide .example .example-rendered {
  padding: 6px;
  background: #282828;
  overflow: hidden;
}
.styleguide .example .example-code {
  background-color: #262626;
}
.styleguide .example .example-code pre {
  border: none;
  background-color: inherit;
}
