css:
.transition-03 {
  transition: 0.3s ease;
}
body {
  font-size: 12px;
  color: #c0c5ce;
  background-color: #2b303b;
}
atom-pane {
  border-right: 1px solid #3f444f;
}
atom-pane:last-child {
  border-right: none;
}
atom-pane-container atom-pane .item-views > * {
  top: 5px;
  bottom: 5px;
}
.icon::before {
  position: relative;
  top: 1px;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar {
  width: 5px;
  height: 5px;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-thumb {
  border-radius: 5px;
  border: 2px solid transparent;
  background: #3f444f;
  background-clip: content-box;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-thumb:vertical:active {
  border-radius: 0;
  border-left-width: 0;
  border-right-width: 0;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-thumb:horizontal:active {
  border-radius: 0;
  border-top-width: 0;
  border-bottom-width: 0;
}
code {
  color: #868e96;
}
.btn {
  color: #d1d5db;
  background-color: #414859;
}
.btn:focus,
.btn:hover {
  color: #212529;
  background-color: #5a657c;
}
.btn:active {
  color: rgba(206, 212, 218, 0.66);
  background-color: #3c4353;
}
.btn.selected {
  color: #ced4da;
  background-color: #5a657c;
}
.btn:focus,
.btn:focus:active {
  outline: none;
}
.btn .icon::before {
  position: relative;
  top: 1px;
}
.btn.btn-primary {
  color: #ced4da;
  background-color: #212529;
}
.btn.btn-primary:focus,
.btn.btn-primary:hover {
  background-color: #2a2f34;
}
.btn.btn-primary:active {
  background-color: #181b1e;
}
.btn.btn-primary.selected {
  background-color: #2a2f34;
}
.btn.btn-primary.selected:focus,
.btn.btn-primary.selected:hover {
  background-color: #333940;
}
.btn.btn-info {
  color: #ced4da;
  background-color: #a3be8c;
}
.btn.btn-info:focus,
.btn.btn-info:hover {
  background-color: #adc599;
}
.btn.btn-info:active {
  background-color: #99b77f;
}
.btn.btn-info.selected {
  background-color: #adc599;
}
.btn.btn-info.selected:focus,
.btn.btn-info.selected:hover {
  background-color: #b8cda6;
}
.btn.btn-success {
  color: #ced4da;
  background-color: #212529;
}
.btn.btn-success:focus,
.btn.btn-success:hover {
  background-color: #2a2f34;
}
.btn.btn-success:active {
  background-color: #181b1e;
}
.btn.btn-success.selected {
  background-color: #2a2f34;
}
.btn.btn-success.selected:focus,
.btn.btn-success.selected:hover {
  background-color: #333940;
}
.btn.btn-warning {
  color: #ced4da;
  background-color: #fd7e14;
}
.btn.btn-warning:focus,
.btn.btn-warning:hover {
  background-color: #fd8928;
}
.btn.btn-warning:active {
  background-color: #fa7302;
}
.btn.btn-warning.selected {
  background-color: #fd8928;
}
.btn.btn-warning.selected:focus,
.btn.btn-warning.selected:hover {
  background-color: #fd943c;
}
.btn.btn-error {
  color: #ced4da;
  background-color: #fa5252;
}
.btn.btn-error:focus,
.btn.btn-error:hover {
  background-color: #fb6666;
}
.btn.btn-error:active {
  background-color: #f93e3e;
}
.btn.btn-error.selected {
  background-color: #fb6666;
}
.btn.btn-error.selected:focus,
.btn.btn-error.selected:hover {
  background-color: #fb7a7a;
}
.btn-group > .btn {
  transition: 0.3s ease;
  border-color: transparent;
}
.btn-group > .btn:hover {
  color: #ced4da;
  background: #303642;
}
body {
  transition: all 125ms cubic-bezier(0.215, 0.61, 0.355, 1);
}
atom-pane {
  padding: 25px;
}
atom-text-editor[mini] {
  padding-left: 5px;
  font-size: 1rem;
  color: #c0c5ce;
  background-color: #2b303b;
  border-bottom: 1px solid #3f444f;
}
atom-text-editor[mini].is-focused .cursor {
  display: block;
}
atom-text-editor[mini] .placeholder-text {
  color: #868e96;
}
atom-text-editor[mini] .selection .region {
  background-color: #363c4a;
}
atom-text-editor[mini] .cursor {
  display: none;
}
.find-and-replace,
.project-find {
  border-top: 1px solid #3f444f;
}
.find-and-replace *,
.project-find * {
  z-index: 4;
}
.status {
  color: #c0c5ce;
}
.status-added {
  color: #a3be8c;
}
.status-ignored {
  color: #65737e;
}
.status-modified {
  color: #ebcb8b;
}
.status-removed {
  color: #bf616a;
}
.status-renamed {
  color: #8fa1b3;
}
.key-binding {
  display: inline-block;
  margin-left: 1px;
  padding: 0 5px;
  line-height: 2;
  font-family: 'Helvetica Neue', Arial, sans-serif;
  font-size: .8em;
  letter-spacing: 1px;
  border-radius: 2px;
  background-color: #2b303b;
}
.command-palette .list-group .character-match,
.fuzzy-finder .list-group .character-match {
  color: #a3be8c;
}
.list-group li {
  padding-left: 5px;
  border-bottom: 1px dotted #3f444f;
  color: #e9ecef;
  transition: all 125ms cubic-bezier(0.215, 0.61, 0.355, 1);
}
.list-group li.selected {
  color: #a3be8c;
  z-index: 15;
}
.list-group li.selected:before {
  display: none;
}
.list-tree .selected {
  color: #ced4da;
}
.select-list li {
  padding: 7.5px 10px;
}
.select-list .status {
  float: right;
}
.select-list .character-match {
  font-weight: normal !important;
  border-bottom: 1px solid #a3be8c;
  color: #c0c5ce;
}
autocomplete-suggestion-list.select-list.popover-list {
  padding: 5px;
  border-radius: 2px;
  background-color: #2b303b;
  box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.1), 0 0 1px 0 rgba(0, 0, 0, 0.14);
}
autocomplete-suggestion-list.select-list.popover-list .suggestion-description {
  background: transparent;
  font-size: .9rem;
}
autocomplete-suggestion-list.select-list.popover-list .suggestion-description-content {
  font-size: .9rem;
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group li {
  line-height: 2em;
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group li.selected .character-match {
  color: #a3be8c;
}
autocomplete-suggestion-list.select-list.popover-list .selected {
  background: #3b404b;
}
autocomplete-suggestion-list.select-list.popover-list .selected .icon {
  color: #a3be8c;
}
.select-list .selected .secondary-line {
  color: #868e96;
}
.list-group li:not(.list-nested-item) {
  line-height: 1.8;
}
autocomplete-suggestion-list .suggestion-list-scroller {
  background: #2b303b;
}
autocomplete-suggestion-list .left-label {
  color: #868e96;
}
autocomplete-suggestion-list .word-container {
  color: #adb5bd;
}
autocomplete-suggestion-list .icon-container .icon {
  background: rgba(255, 255, 255, 0.5) !important;
  color: #ced4da;
}
.panel-heading {
  border-bottom: 1px solid #3f444f;
  background-color: #2b303b;
}
.panel-body a {
  color: #8fa1b3;
}
.panel-body a:hover {
  color: #96b5b4;
}
.inset-panel {
  background-color: #2b303b;
}
.inset-panel .panel-heading {
  background-color: #343a47;
}
atom-panel.modal {
  position: absolute;
  border: 1px solid #3f444f;
  border-top: none;
  top: 0;
  left: 0;
  margin-left: 0;
  width: 100%;
  height: 100%;
  z-index: 100;
  display: flex;
  justify-content: center;
  align-items: center;
  background: rgba(43, 48, 59, 0.96);
}
atom-panel.modal > .select-list {
  min-width: 500px;
}
.inset-panel {
  border-radius: 0;
}
.deprecation-info.list-item span {
  color: #96b5b4;
}
.deprecation-cop a {
  color: #8fa1b3;
}
.loading-spinner-large {
  display: block;
  width: 64px;
  height: 64px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 64px 64px;
}
.loading-spinner-large.inline-block {
  display: inline-block;
}
.loading-spinner-medium {
  display: block;
  width: 48px;
  height: 48px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 48px 48px;
}
.loading-spinner-medium.inline-block {
  display: inline-block;
}
.loading-spinner-small {
  display: block;
  width: 32px;
  height: 32px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 32px 32px;
}
.loading-spinner-small.inline-block {
  display: inline-block;
}
.loading-spinner-tiny {
  display: block;
  width: 16px;
  height: 16px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 16px 16px;
}
.loading-spinner-tiny.inline-block {
  display: inline-block;
}
.settings-view .section .section-heading {
  color: #c0c5ce;
}
.settings-view .link {
  color: #8fa1b3;
}
.settings-view .link:hover {
  color: #96b5b4;
}
.settings-view .alert {
  border: none;
}
.settings-view .config-menu {
  background: #2b303b;
}
.settings-view .config-menu .panels-menu li a {
  transition: 0.3s ease;
}
.settings-view .config-menu .panels-menu li.active a {
  background: #363c4a;
}
.settings-view .config-menu .panels-menu li:hover {
  background: #313744;
}
.settings-view .package-card {
  transition: 0.3s ease;
  background: #363c4a;
  border: none;
}
.settings-view .package-card:hover {
  background: #313744;
}
.settings-view .package-card .package-name {
  color: #8fa1b3;
}
.ui-site-1 {
  background-color: #0088ff;
}
.ui-site-2 {
  background-color: #20b684;
}
.ui-site-3 {
  background-color: #cc8533;
}
.ui-site-4 {
  background-color: #d831b0;
}
.ui-site-5 {
  background-color: #ebdd5b;
}
.status-bar {
  font-size: 12px;
  color: #c0c5ce;
  border-top: 1px solid #3f444f;
  background-color: #2b303b;
  opacity: 0;
  transition: opacity 150ms ease-out;
  z-index: 3;
}
.status-bar:hover {
  opacity: 1;
  z-index: 5;
}
.status-bar .deprecation-number {
  color: #b48ead;
}
.status-bar .package-updates-status-view {
  color: #96b5b4;
}
.status-bar .package-updates-status-view:hover {
  border-bottom-color: #96b5b4;
}
.footer {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
}
.title {
  text-transform: uppercase;
  font-size: 10px;
  letter-spacing: 1px;
}
.transition {
  transition: all 125ms cubic-bezier(0.215, 0.61, 0.355, 1);
}
.small-shadow {
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 0 1px 0 rgba(0, 0, 0, 0.14);
}
.medium-shadow {
  box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.1), 0 0 1px 0 rgba(0, 0, 0, 0.14);
}
.large-shadow {
  box-shadow: 0 5px 32px 0 rgba(0, 0, 0, 0.1);
}
atom-pane:not(.active) .tab-bar .tab.active {
  color: #868e96;
}
.platform-darwin atom-workspace atom-panel-container.left:empty + atom-workspace-axis.vertical .tab-bar {
  padding-left: 0 !important;
}
.tab-bar {
  background-color: #2b303b;
  margin: 15px;
  text-transform: uppercase;
  font-size: 10px;
  letter-spacing: 1px;
}
.tab-bar .tab {
  color: #868e96;
  height: 34px;
  line-height: 34px;
  font-size: 12px;
  margin-top: -1px;
  max-width: 200px;
  text-transform: uppercase;
  font-size: 11px;
  transition: all 150ms ease-out;
}
.tab-bar .tab .title .icon::before {
  font-size: 13px;
}
.tab-bar .tab.active {
  flex: 1;
  color: #ced4da;
  font-weight: bold;
}
.tab-bar .tab.active .title {
  padding: 0;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  border: 2px solid #a3be8c;
  opacity: 1;
  top: 50%;
  right: 14px;
  margin-top: -3px;
}
.tab-bar .tab.modified:hover .close-icon {
  color: currentColor;
}
.tab-bar .tab .close-icon {
  opacity: 0;
}
.tab-bar .tab .close-icon::before {
  text-align: center;
}
.tab-bar .tab .close-icon:hover {
  color: #ced4da;
}
.tab-bar .tab.active .close-icon:hover {
  color: #c0c5ce;
}
.tab-bar .tab.is-dragging {
  background: #16181d;
  opacity: 0.5;
}
.tab-bar .tab.is-dragging .close-icon {
  visibility: hidden;
}
.tab-bar .tab:hover .close-icon {
  opacity: 1;
}
.text-smaller {
  font-size: smaller;
}
.text-subtle {
  color: #868e96;
}
.text-highlight {
  color: #212529;
}
.text-info {
  color: #228ae6;
}
.text-success {
  color: #212529;
}
.text-warning {
  color: #12b886;
}
.text-error {
  color: #fa5252;
}
.highlight {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #313744;
  color: #212529;
}
.highlight-info {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #a3be8c;
  color: #212529;
}
.highlight-warning {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #fd7e14;
  color: #212529;
}
.highlight-error {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #bf616a;
  color: #212529;
}
.highlight-success {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #a3be8c;
  color: #212529;
}
.background-message {
  font-weight: bold;
  color: #868e96;
}
.tooltip .tooltip-inner {
  line-height: 1;
  padding: 0.75em;
  white-space: nowrap;
  max-width: none;
  color: #FFF;
}
.tooltip .keystroke {
  padding: 0.15em 0.4em;
  margin: 0 -0.3em 0 0.25em;
  color: #FFF;
  background: #363c4a;
}
.tree-view-resizer {
  border-top: 1px solid #3f444f;
  border-right: 1px solid #3f444f;
  margin-left: 15px;
  margin-top: 15px;
  border: none;
}
.tree-view {
  color: #c0c5ce;
  background-color: #2b303b;
}
.tree-view .list-item::before {
  color: #65737e;
}
.tree-view .list-item.selected {
  color: #ced4da;
}
.tree-view .list-item.selected::before {
  background: #363c4a;
}
.status-bar .linter-highlight.status-success {
  color: #a3be8c;
}
.status-bar .linter-highlight.status-error {
  color: #bf616a;
}
